vec4 Effect2(vec2 uv) {
    vec4 result;

    // Create a base pattern using noise and time
    float base = noise(vec3(uv * 10.0, iTime)) * 0.5 + 0.5;

    // Add smooth sine wave transitions
    float timeFactor = sin(iTime * 0.5) * 0.5 + 0.5;
    float pattern = base + sin(uv.x * 10.0 + iTime) * 0.3;

    // Create a color transition using HSL
    vec3 color = hsl2rgb(vec3(
        (uv.x + uv.y + timeFactor) * 360.0,
        0.7,
        0.5
    ));

    // Add turbulence for more dynamic effect
    float turb = turbulent(vec2(uv.x + sin(iTime), uv.y)) * 0.3;

    // Combine all elements into the final color
    result.rgb = color * (pattern + turb);
    result.a = 1.0;

    return result;
}
