vec4 Effect2(vec2 uv) {
    // Create a dark blue background with noise texture
    vec3 bg = mix(vec3(0.1, 0.15, 0.2), vec3(0.0), uv.y * 0.5 + 0.5);

    // Add moon glow using noise and turbulence
    float moonGlow = noise(vec3(uv * 0.5 + iTime * 0.1, iTime)) * 0.2;
    bg += vec3(0.9, 0.85, 0.7) * moonGlow;

    // Create owl shape with procedural noise
    float dist = length(uv - vec2(sin(iTime)*0.4 + 0.5, cos(iTime*0.7)*0.3 + 0.6));
    float owlShape = smoothstep(0.15, 0.0, dist);

    // Add wing animation
    float wingFlap = sin(iTime * 2.0) * 0.4;
    vec2 wingPos = uv - vec2(sin(iTime)*0.3 + 0.5, cos(iTime*0.7)*0.3 + 0.6);
    float wingShape = smoothstep(0.15, 0.0, length(wingPos));

    // Create stage lights with turbulence
    float stageLight = turbulent(vec2(uv.x * 4.0, uv.y - 0.8)) * 0.3;
    bg += vec3(0.7, 0.5, 0.1) * stageLight;

    // Combine elements with color transformations
    vec3 finalColor = hsl2rgb(vec3(
        (moonGlow + wingShape) * 180.0,
        0.6 + moonGlow * 0.4,
        0.5 + stageLight * 0.3
    ));

    return vec4(finalColor, 1.0);
}
