vec4 Effect2(vec2 uv) {
    // Create a value that changes with position and time
    float pattern = sin(uv.x * 10.0 + iTime * 5.0) * 
                   cos(uv.y * 10.0 + iTime * 3.0);

    // Add some noise for complexity
    vec2 noiseUV = uv * 4.0 + vec2(iTime, 0.0);
    float turbulence = turbulent(noiseUV);

    // Combine the patterns and add time-based variation
    float colorValue = (pattern + turbulence) * 0.5 + 0.5;

    // Convert to HSL color space for smooth transitions
    vec3 hslColor = vec3(colorValue, 1.0, 0.5);
    vec3 rgbColor = hsl2rgb(hslColor);

    return vec4(rgbColor, 1.0);
}
