vec4 Effect2(vec2 uv) {
    float time = iTime * 0.5; // Animation speed

    // Generate noise patterns with different scales
    float n1 = noise(uv * 100.0 + time);
    float n2 = noise(uv * 50.0 + time * 0.7);
    float n3 = noise(uv * 25.0 + time * 0.4);

    // Combine noise with sine waves for pulsating effect
    vec3 color;
    color.r = sin(n1 * pi + time) * 0.5 + 0.5;
    color.g = sin(n2 * pi + time * 0.9) * 0.5 + 0.5;
    color.b = sin(n3 * pi + time * 0.6) * 0.5 + 0.5;

    // Add turbulence for more dynamic movement
    vec3 turb = vec3(turbulent(uv * 10.0 + time), 
                    turbulent(uv * 12.0 + time * 0.8),
                    turbulent(uv * 14.0 + time * 0.6));

    // Combine color and turbulence with HSL conversion
    vec3 finalColor = hsl2rgb(vec3(
        (color.r + turb.x) * 0.5,
        (color.g + turb.y) * 0.7,
        (color.b + turb.z) * 0.6
    ));

    return vec4(finalColor, 1.0);
}
