vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar form
    vec2 centered = uv * 2.0 - 1.0;
    float theta = atan2(centered.y, centered.x);
    float r = length(centered);

    // Add time-based rotation
    theta += iTime * 0.5; // Rotate at 0.5 radians per second

    // Create color gradient based on angle and radius
    vec3 color = hsl(
        (theta / (2.0 * PI)) + (r * 0.2), // Hue: rotate with time and vary with radius
        0.7,                              // Saturation
        0.5                               // Lightness
    );

    // Add some turbulence to the color transitions
    float turb = turbulent(uv * 10.0 + iTime);
    color = hsl2rgb(vec3(
        (theta / (2.0 * PI)) + (r * 0.2) + (turb * 0.3), // Add turbulence to hue
        0.7,
        0.5
    ));

    return vec4(color, 1.0);
}
