vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar for rotation effect
    vec2 st = uv * 2.0 - 1.0;
    float angle = atan(st.y, st.x) + iTime; // Add time-based rotation

    // Create a turbulent pattern with pulsating colors
    float t = turbulent(vec3(uv * 5.0, iTime * 0.5));

    // Generate color using HSL to RGB conversion
    vec3 color = hsl(
        mod(angle * 0.2 + iTime * 0.1, 1.0), // Hue variation over time and space
        0.7 + t * 0.3,                       // Saturation modulation
        0.5 + sin(iTime) * 0.4               // Brightness pulsation
    );

    return vec4(color, 1.0);
}
