vec4 Effect2(vec2 uv) {
    // Remap UV to center-based coordinates
    vec2 st = uv - 0.5;

    // Add time-based rotation
    float theta = atan2(st.y, st.x) + sin(iTime * 0.5) * 3.0;
    float r = length(st);

    // Create base color with rotating hues
    vec3 color = hsl2rgb(vec3(
        theta * 0.16 + iTime * 0.2,   // Hue rotation over time
        0.7 + turbulent(vec2(r, iTime)) * 0.3, // Saturation variation
        0.5 + sin(iTime) * 0.2         // Brightness variation
    ));

    // Add noise and turbulence effects
    float noise1 = turbulent(vec2(theta, r * 4.0));
    float noise2 = turbulent(vec2(r * 3.0 + iTime, theta * 2.0));

    // Combine colors with mix function
    color = mix(color, hsl2rgb(vec3(
        theta * 0.16 + iTime * 0.3,
        0.5,
        0.7 + sin(iTime) * 0.2
    )), noise1 * 0.5);

    // Add final variations
    color *= 1.0 + noise2 * 0.3;
    color *= 1.0 - abs(r - 0.5) * 0.8; // Distance falloff

    return vec4(color, 1.0);
}
