vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar form
    vec2 st = uv * 2.0 - 1.0;
    float r = length(st);
    float theta = atan2(st.y, st.x);
    // Add rotation based on time
    theta += iTime * 0.5;
    // Create hexagon shape using polar coordinates
    float hexAngle = theta + radians(30.0); // Offset for hexagon points
    float hexDistance = abs(sin(hexAngle * 6.0)) / (1.0 - r * 0.5);

    // Add pulsating effect
    float pulse = sin(iTime) * 0.2;
    float radius = r + pulse;
    // Create color shifts using HSL
    vec3 hslColor = vec3(
        iTime * 0.1,          // Hue
        0.7 + turbulent(uv * 4.0), // Saturation with noise
        0.5                  // Lightness
    );

    // Convert to RGB and add pulsation effect
    vec3 rgbColor = hsl2rgb(hslColor);
    rgbColor *= sin(iTime) * 0.5 + 0.75;
    // Create glowing border effect
    float edge = abs(dFdx(radius)) + abs(dFdy(radius));
    edge = smoothstep(0.0, 1.0, edge * 2.0);

    // Combine effects and output final color
    vec3 finalColor = mix(
        rgbColor,
        vec3(1.0),
        edge * 0.5
    );

    return vec4(finalColor, 1.0);
}
