vec4 Effect2(vec2 uv) {
    // Create rotating sine wave patterns
    vec3 color = vec3(
        sin(uv.x * 10.0 + iTime) * 0.5 + 0.5,
        sin(uv.y * 10.0 + iTime * 0.9) * 0.5 + 0.5,
        sin((uv.x + uv.y) * 10.0 + iTime * 0.8) * 0.5 + 0.5
    );

    // Add color variation using HSL conversion
    vec3 hslColor = hsl(
        sin(iTime * 0.2 + uv.x * 2.0 + uv.y * 3.0) * 0.5 + 0.5,
        0.7,
        0.6
    );

    // Combine patterns and convert to RGB
    color = mix(color, hsl2rgb(hslColor), 0.5);

    return vec4(color, 1.0);
}
