vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar coordinates
    vec2 st = uv * 2.0 - 1.0;
    float r = length(st);
    float theta = atan2(st.y, st.x) + iTime * 0.5;

    // Create a noise-based pattern with time variation
    float noiseValue = noise(vec3(st * 4.0, iTime * 0.1));
    r += noiseValue * 0.2;

    // Create color transitions using HSL
    vec3 color = hsl(theta * 0.5 + 0.5, 0.8, 0.6);

    // Add geometric pattern overlay
    float pattern = sin(r * 10.0) * cos(theta * 3.0) * 0.5;
    pattern += sin(r * 5.0) * cos(theta * 5.0) * 0.3;

    // Combine patterns and colors
    vec3 finalColor = color + vec3(pattern);

    return vec4(finalColor, 1.0);
}
