vec4 Effect2(vec2 uv) {
    // Calculate position relative to center
    vec2 pos = uv - 0.5;

    // Convert to polar coordinates
    float angle = atan2(pos.y, pos.x);
    float radius = length(pos);

    // Add time-based rotation
    angle += iTime * 0.5; // Adjust speed by changing 0.5

    // Create smooth color transitions using HSL
    vec3 hslColor;
    hslColor.x = fract(angle / (2.0 * PI) + 0.5); // Hue based on angle
    hslColor.y = 1.0;                            // Full saturation
    hslColor.z = 0.5 + turbulent(vec2(radius)) * 0.4; // Add noise to value

    // Convert HSL to RGB and output
    return vec4(hsl2rgb(hslColor), 1.0);
}
