vec4 Effect2(vec2 uv) {
    // Create flowing sine wave pattern with dynamic colors

    // Transform UV coordinates for larger scale
    vec2 st = (uv - 0.5) * 3.0;

    // Add time-based movement
    float time = iTime * 0.5;

    // Calculate sine waves in both directions
    float waveX = sin(st.x + time) * 0.5 + 0.5;
    float waveY = sin(st.y + time * 0.7) * 0.5 + 0.5;

    // Combine waves and add noise
    float pattern = (waveX + waveY) * 0.5;
    pattern += turbulent(vec2(st.x * 0.5, st.y * 0.5)) * 0.3;

    // Create color transitions using HSL
    vec3 colorHSL = vec3(
        fract(time * 0.1 + pattern * 0.2) * 360.0,
        0.7,
        0.5 + sin(time * 0.3 + pattern * 0.4) * 0.2
    );

    // Convert to RGB and output
    vec3 rgbColor = hsl2rgb(colorHSL);

    return vec4(rgbColor * (sin(pattern * 10.0 + time) * 0.5 + 0.5), 1.0);
}
