vec4 Effect2(vec2 uv) {
    // Convert UV to polar coordinates
    vec2 st = uv * 2.0 - 1.0;
    float r = length(st);
    float a = atan2(st.y, st.x);
    // Create pulsating effect using sine wave
    float pulse = sin(iTime * 3.0) * 0.5 + 0.5;
    float radius = r * (1.0 + sin(iTime * 2.0)) * 0.5;
    // Calculate base color using HSL
    vec3 hslColor = vec3(
        a / (2.0 * PI) + 0.5 + iTime * 0.1,
        0.7,
        0.6
    );
    // Add turbulence to create interesting patterns
    float noiseValue = turbulent(vec3(uv * 4.0, iTime));
    hslColor.x += noiseValue * 0.2;
    radius += noiseValue * 0.1;
    // Convert HSL to RGB and mix with pulsating white
    vec3 color = hsl2rgb(hslColor);
    color = mix(color, vec3(1.0), pulse * 0.5);
    // Add brightness variation
    float brightness = sin(iTime) * 0.5 + 0.7;
    color *= brightness;
    return vec4(color, 1.0);
}
