vec4 Effect2(vec2 uv) {
    // Create pulsating grid pattern using sine waves
    vec2 st = uv * 10.0; // Scale UV coordinates

    // Add time-based oscillation
    float time = iTime * 0.5;

    // Calculate grid lines with pulsing effect
    float xLine = sin(st.x + time) * cos(time);
    float yLine = cos(st.y + time) * sin(time);

    // Combine lines and add turbulence for dynamic effect
    float pattern = sin(xLine + yLine + time) * 0.5 + 0.5;
    pattern += turbulent(vec2(st.x, st.y)) * 0.3;

    // Create color shift over time using HSL
    vec3 color = hsl(pattern * 360.0, 1.0, 0.5);
    color = hsl2rgb(color);

    return vec4(color, 1.0);
}
