vec4 Effect2(vec2 uv) {
    // Scale and offset coordinates for better pattern distribution
    vec2 st = uv * 5.0 - 2.7;

    // Create a moving position vector with time component
    vec3 pos = vec3(st.x + iTime*0.5, st.y + iTime*0.3, sin(iTime)*1.5);

    // Generate noise and turbulence values
    float n = noise(pos);
    float t = turbulent(vec2(uv * 4.0 + iTime));

    // Calculate hue based on position and time
    float hue = atan2(st.y, st.x) / (2.0 * PI) + 0.5;
    hue += sin(iTime * 0.3) * 0.5;

    // Create HSL color from components
    vec3 hslColor = vec3(hue, 1.0 - abs(n), 0.6 + t*0.4);
    vec3 rgbColor = hsl2rgb(hslColor);

    return vec4(rgbColor, 1.0);
}
