vec4 Effect2(vec2 uv) {
    // Create a grid of squares
    float numTiles = 15.0;
    vec2 tilePos = floor(uv * numTiles);

    // Calculate center of each square
    vec2 center = (tilePos + 0.5) / numTiles;

    // Calculate distance from center
    vec2 toCenter = uv - center;
    float dist = length(toCenter);

    // Create rotation effect
    float angle = iTime * 0.5 + tilePos.x * 1.7 + tilePos.y * 3.4;
    angle += noise(vec3(tilePos, iTime)) * 2.0;
    vec2 rotated = vec2(
        toCenter.x * cos(angle) - toCenter.y * sin(angle),
        toCenter.x * sin(angle) + toCenter.y * cos(angle)
    );

    // Calculate color
    float hue = fract(iTime * 0.1 + tilePos.x * 0.3 + tilePos.y * 0.4);
    vec3 color = hsl2rgb(vec3(hue, 1.0, 0.5));

    // Add pulsing effect based on distance
    float pulse = sin(iTime + dist * 10.0) * 0.5 + 0.5;
    color *= pulse;

    return vec4(color, 1.0);
}
