vec4 Effect2(vec2 uv) {
    // Create flowing noise patterns
    vec2 st = uv * 5.0 + iTime;
    float n1 = noise(st);
    float n2 = noise(st + vec2(1.0, 2.0));

    // Add turbulence for more complex flow
    float turb = turbulent(vec3(uv * 4.0, iTime * 0.5));

    // Combine patterns with different scales
    float pattern = (n1 + n2) * 0.5 + turb * 0.3;

    // Create color shifts using HSL
    vec3 hslColor = vec3(
        fract(iTime * 0.1 + uv.x * 2.0),
        0.7,
        0.5 + pattern * 0.3
    );

    // Convert to RGB and output
    return vec4(hsl2rgb(hslColor), 1.0);
}
