vec4 Effect2(vec2 uv) {
    // Create rotating patterns using sine waves
    vec2 st = uv * 5.0 + vec2(sin(iTime)*3.0, cos(iTime)*3.0);

    // Add time-based rotation
    float angle = iTime * 0.1;
    st = mat2(cos(angle), -sin(angle),
              sin(angle), cos(angle)) * st;

    // Create color transitions using HSL
    vec3 color = hsl2rgb(vec3(0.5 + sin(iTime + st.x) * 0.5,
                              0.7, 0.8));

    // Add noise and turbulence for depth
    float turb = turbulent(st);
    color *= 1.0 - turb * 0.5;

    return vec4(color, 1.0);
}
