vec4 Effect2(vec2 uv) {
    // Convert to polar coordinates
    float r = length(uv);
    float a = atan2(uv.y, uv.x);

    // Create flowing sine wave patterns
    float pattern1 = sin(r * 5.0 + iTime) * cos(a * 3.0 + iTime * 0.5);
    float pattern2 = sin(r * 4.0 - iTime) * sin(a * 6.0 + iTime * 0.7);

    // Combine patterns with noise
    float noise1 = noise(vec2(r * 2.0, a));
    float noise2 = noise(vec2(r * 3.0, a + iTime));

    // Create turbulence effect
    float turbulentValue = turbulent(vec2(r * 4.0, a)) * 0.5 + 0.5;

    // Combine all elements
    float finalPattern = (pattern1 + pattern2) * 0.5 + noise1 * noise2 * turbulentValue;

    // Convert to color using HSL
    vec3 hslColor = vec3(finalPattern * 0.7 + 0.3, 0.8, 0.6);
    vec3 rgbColor = hsl2rgb(hslColor);

    return vec4(rgbColor, 1.0);
}
