vec4 Effect2(vec2 uv) {
    // Create sine wave patterns with varying frequencies
    float freq = 5.0;
    vec2 st = uv * freq;

    // Add time-based phase shift
    float timePhase = iTime * 0.1;

    // Calculate sine waves in both directions
    float xSine = sin(st.x + timePhase) * 0.5 + 0.5;
    float ySine = sin(st.y + timePhase * 2.0) * 0.5 + 0.5;

    // Combine patterns using multiplication and addition
    float pattern = xSine * ySine + (xSine + ySine) * 0.5;

    // Add noise for extra complexity
    vec3 noiseParams = vec3(uv * 10.0, iTime);
    float noiseValue = noise(noiseParams);
    pattern += noiseValue * 0.2;

    // Create color shifts using HSL
    vec3 hslColor = vec3(
        fract(iTime * 0.5) + 0.5,
        1.0 - abs(pattern - 0.5) * 2.0,
        pattern
    );

    // Convert to RGB and mix with noise for depth
    vec3 color = hsl2rgb(hslColor);
    color += noiseValue * 0.3;

    return vec4(color, 1.0);
}
