vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar form
    vec2 st = uv * 2.0 - 1.0;
    float theta = atan(st.y, st.x);
    float r = length(st);

    // Create hexagonal rotation pattern
    theta += iTime * 0.5;
    theta *= 6.0; // Hexagon symmetry

    // Add noise and turbulence
    vec2 noiseUV = uv + vec2(turbulent(vec3(uv * 10.0, iTime)));
    float noiseVal = noise(noiseUV);

    // Create color pattern with shifting hues
    float hue = theta * 0.5 + iTime;
    vec3 hslColor = vec3(hue, sin(r * 2.0 + iTime) * 0.5 + 0.5, 0.8);
    vec3 rgbColor = hsl2rgb(hslColor);

    // Combine patterns
    float pattern = abs(sin(theta)) * r;
    pattern += noiseVal * 0.3;

    // Final color output
    return vec4(rgbColor * (1.0 - pattern), 1.0);
}
