vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar
    float r = length(uv);
    float theta = atan2(uv.y, uv.x);

    // Pulsating ring parameters
    float baseRadius = 0.7;
    float pulsation = sin(iTime * 0.5) * 0.5;
    float ringWidth = 0.15;

    // Dynamic color shifting
    vec3 innerColor = hsl2rgb(vec3((sin(iTime * 0.5) + 1.0) / 2.0, 0.8, 0.6));
    vec3 outerColor = hsl2rgb(vec3((cos(iTime * 0.5) + 1.0) / 2.0, 0.7, 0.4));

    // Create inner glow effect
    float innerGlow = smoothstep(0.0, 0.1, abs(r - (baseRadius - ringWidth)));

    // Create outer glow effect  
    float outerGlow = smoothstep(0.0, 0.15, abs(r - (baseRadius + ringWidth + pulsation)));

    // Combine effects with color
    vec3 finalColor = mix(
        innerColor * sin(theta * 8.0 + iTime) * 0.5 + innerColor * 0.5,
        outerColor * sin(theta * 4.0 + iTime) * 0.5 + outerColor * 0.5,
        smoothstep(baseRadius - ringWidth, baseRadius + ringWidth, r)
    );

    // Add pulsation effect
    finalColor *= 1.0 + sin(iTime) * 0.3;

    return vec4(finalColor, 1.0);
}
