vec4 Effect2(vec2 uv) {
    // Remap coordinates from 0-1 range to -1-1 range for easier manipulation
    vec2 st = uv * 2.0 - 1.0;

    // Convert to polar coordinates
    float theta = atan2(st.y, st.x);
    float r = length(st) * 4.0; // Scale radius

    // Add time-based rotation
    theta += 8.0 * iTime;

    // Create rotating sine wave pattern
    float wave1 = sin(r + theta * 5.0 + 2.0 * iTime);
    float wave2 = sin(theta - r * 3.0 + 4.0 * iTime);
    float value = (wave1 + wave2) / 2.0;

    // Add some frequency modulation
    value += sin(r * 10.0 + theta * 10.0 + 8.0 * iTime) * 0.5;

    // Create color from HSL values with time-based shifting
    float hue = theta / (2.0 * PI);
    float saturation = 0.7 + turbulent(vec3(st.x, st.y, iTime)) * 0.3;
    float lightness = 0.5 + value * 0.4;

    // Add some noise to the color
    vec3 color = hsl(hue, saturation, lightness);
    color += vec3(turbulent(vec3(st.x*2.0, st.y*2.0, iTime)) * 0.15);

    return vec4(color, 1.0);
}
