vec4 Effect2(vec2 uv) {
    // Create flowing movement pattern
    vec2 st = uv * 5.0 + vec2(sin(iTime)*0.5, cos(iTime*0.7)*0.5);

    // Add turbulence at different scales
    float t1 = turbulent(st * 0.5);
    float t2 = turbulent(st * 1.5);

    // Combine turbulence layers
    float noiseValue = (t1 + t2) * 0.5;

    // Create color transitions using HSL
    vec3 hslColor = vec3(
        sin(iTime*0.5 + noiseValue*2.0)*0.5 + 0.5,  // Hue variation
        0.7,  // Saturation
        abs(noiseValue) * 0.6 + 0.2  // Lightness modulation
    );

    // Convert to RGB and output
    return vec4(hsl2rgb(hslColor), 1.0);
}
