vec4 Effect2(vec2 uv) {
    // Scale UV coordinates for grid pattern
    vec2 st = uv * 10.0;

    // Get cell position using floor function
    vec2 pos = floor(st);

    // Calculate distance from center of each cell
    vec2 center = (pos + 0.5) / 10.0;
    float dist = length(uv - center);

    // Create pulsing animation using sine and cosine functions
    float time = iTime * 0.5;
    float pulse = sin(time + pos.x * 3.1415926) * cos(time + pos.y * 3.1415926);

    // Generate color pattern using HSL to RGB conversion
    vec3 hslColor = vec3(
        (pos.x + time) * 0.1,
        0.7 + sin(time + pos.y * 3.1415926) * 0.3,
        0.5 + pulse * 0.5
    );

    // Convert HSL to RGB and add noise for glowing effect
    vec3 color = hsl2rgb(hslColor);
    color *= 1.0 + turbulent(vec3(st, time)) * 0.5;

    // Create smooth transitions between cells using distance
    float blend = smoothstep(0.4, 0.6, dist);

    // Mix colors based on distance from cell center
    vec3 finalColor = mix(color, vec3(1.0), blend);

    return vec4(finalColor, 1.0);
}
