vec4 Effect2(vec2 uv) {
    // Convert UV to polar coordinates
    vec2 st = uv * 2.0 - 1.0;
    float r = length(st);
    float a = atan2(st.y, st.x);
    // Add time-based movement and turbulence
    float time = iTime * 0.5;
    float radius = r + sin(time) * 0.3;
    float angle = a + time;
    // Create color variations using HSL
    vec3 baseColor = hsl2rgb(vec3(0.5 + sin(angle) * 0.4, 0.7, 0.6));
    vec3 mixColor = hsl2rgb(vec3(0.3 + cos(angle) * 0.4, 0.8, 0.5));
    // Combine colors with noise and turbulence
    float noiseValue = noise(vec3(uv * 10.0, time)) * 0.5 + 0.5;
    vec3 finalColor = mix(baseColor, mixColor, noiseValue);
    return vec4(finalColor, 1.0);
}
