vec4 Effect2(vec2 uv) {
    float time = iTime * 0.5;

    // Animate UV coordinates with sine functions
    vec2 animatedUV = uv + sin(uv * 10.0 + time) * 0.3;
    animatedUV += sin(uv * 5.0 + time * 0.7) * 0.2;

    // Add turbulence for more complex patterns
    float noiseValue = turbulent(animatedUV * 4.0);

    // Create color from noise values using HSL conversion
    vec3 color = hsl2rgb(vec3(
        noiseValue * 360.0,   // Hue
        0.7 + noiseValue * 0.3, // Saturation
        0.5 + sin(time) * 0.4  // Lightness
    ));

    return vec4(color, 1.0);
}
