vec4 Effect2(vec2 uv) {
    // Center coordinates and scale them
    vec2 st = uv * 2.0 - 1.0;

    // Create a base frequency for movement
    float time = iTime * 0.5;

    // Add noise to create organic movement patterns
    vec3 noisePos = vec3(st.x + sin(time), st.y + cos(time * 0.7), time);
    float n1 = noise(noisePos) * 0.5 + 0.5;
    float n2 = noise(vec3(st.x, st.y, time)) * 0.5 + 0.5;

    // Create a flowing pattern using sine waves
    vec2 flow = vec2(
        sin(time + st.x * 4.0) * 0.1,
        cos(time * 0.7 - st.y * 3.0) * 0.1
    );

    // Add turbulence for more complex movement patterns
    float turb = turbulent(vec2(st.x, st.y)) * 0.5 + 0.5;
    flow += vec2(sin(turb * pi), cos(turb * pi)) * 0.05;

    // Combine noise and flow to create final position offset
    vec2 finalPos = st + flow * (n1 + n2);

    // Create a color gradient that shifts over time
    float hue = atan2(finalPos.y, finalPos.x) * 0.5 + time;
    vec3 color = hsl(hue, 0.7, 0.6);

    // Add brightness variation using noise
    color *= n1 * 0.8 + 0.2;

    return vec4(color, 1.0);
}
