vec4 Effect2(vec2 uv) {
    // Create rotating RGB shifts using time-based functions
    vec3 color = hsl(uv.x * 10.0 + iTime, uv.y * 10.0 - iTime, 0.5);

    // Add turbulence and noise for more complex patterns
    vec2 st = uv * 4.0;
    float t = iTime * 0.5;

    // Create rotating pattern
    vec2 pos = uv - 0.5;
    float angle = length(pos) * 10.0 + t * 2.0;
    mat2 rot = mat2(cos(angle), sin(angle), -sin(angle), cos(angle));
    vec2 rotatedPos = pos * rot;

    // Add noise and turbulence
    float noiseVal = noise(vec3(rotatedPos, iTime)) * 0.5 + 0.5;
    float turbulentVal = turbulent(rotatedPos * 10.0) * 0.5 + 0.5;

    // Combine effects for final color
    vec3 finalColor = hsl2rgb(vec3(
        uv.x * 3.14 + t,
        noiseVal,
        turbulentVal
    ));

    return vec4(finalColor, 1.0);
}
