vec4 Effect2(vec2 uv) {
    // Scale and shift UV coordinates for grid pattern
    vec2 st = uv * 10.0 + iTime;

    // Create a grid of cells using fract function
    vec3 color = vec3(fract(sin(st.x)*1000.0), 
                     fract(cos(st.y)*1000.0),
                     fract(sin(st.x+st.y)*1000.0));

    // Add turbulence to create flowing effect
    float turb = turbulent(vec2(uv * 5.0 + iTime*0.5));
    color += vec3(turb);

    // Create rotating pattern using polar coordinates
    float angle = atan2(uv.y - 0.5, uv.x - 0.5) + iTime;
    float radius = length(uv - 0.5);

    // Combine rotation and turbulence effects
    color += vec3(sin(angle)*radius*0.5,
                 cos(angle)*radius*0.5,
                 sin(angle+PI/2.0)*radius*0.5);

    // Final color adjustment using HSL to RGB conversion
    return vec4(hsl2rgb(vec3(
        fract(color.r + iTime),
        0.7 + 0.3*sin(iTime),
        0.5 + 0.5*cos(iTime)
    )), 1.0);
}
