vec4 Effect2(vec2 uv) {
    float time = iTime * 0.5;
    vec3 color = vec3(0.0);

    // Create shifting pattern using noise
    float a = time + uv.x * 10.0;
    float n = noise(vec2(a, uv.y * 10.0));

    // Add pulsing effect
    float m = noise(vec2(time * 5.0, uv.x * 10.0));

    // Combine effects and convert to RGB
    vec3 c = hsl(n * 360.0, 0.7, 0.5);
    color = mix(vec3(0.0), hsl2rgb(c), n * 0.5 + m * 0.3);

    return vec4(color, 1.0);
}
