vec4 Effect2(vec2 uv) {
    // Calculate distance from center
    vec2 st = uv - 0.5;
    float d = length(st);

    // Create rotating pattern using polar coordinates
    float theta = atan2(st.y, st.x) + iTime * 0.5;

    // Add multiple frequencies for concentric circles
    theta += sin(theta * 4.0) * cos(iTime) * 0.5;

    // Create color variations based on distance and angle
    vec3 color = hsl2rgb(vec3(
        mod(d * 10.0 + iTime, 1.0),
        1.0,
        sin(theta * 2.0 + iTime) * 0.5 + 0.5
    ));

    // Add turbulence effect to create dynamic circles
    float turb = turbulent(vec3(uv * 4.0, iTime));
    color *= sin(d * 10.0 + theta * 3.0 + turb * 2.0) * 0.5 + 0.5;

    return vec4(color, 1.0);
}
