vec4 Effect2(vec2 uv) {
    // Scale coordinates for pattern frequency
    vec2 pos = uv * 50.0;

    // Create dynamic noise patterns with time offset
    float noise1 = turbulent(pos + iTime);
    float noise2 = turbulent(pos * 0.5 + iTime * 0.3);

    // Create smooth color transitions using HSL
    vec3 hslColor = vec3(
        sin(pos.x * 0.1 + iTime) * 0.5 + 0.5,  // Hue variation across screen
        0.8 + noise2 * 0.2,                       // Saturation with some noise variation
        0.5 + noise1 * 0.5                        // Lightness modulation by noise
    );

    // Convert to RGB and output
    return vec4(hsl2rgb(hslColor), 1.0);
}
