vec4 Effect2(vec2 uv) {
    // Center coordinates
    vec2 st = uv - 0.5;

    // Polar coordinates
    float r = length(st);
    float theta = atan2(st.y, st.x);

    // Add time-based rotation and expansion
    theta += iTime * 5.0;
    r += sin(iTime) * 2.0;

    // Create swirling pattern using polar coordinates
    vec3 color = hsl2rgb(vec3(
        theta * 0.1 + r * 0.2,   // Hue variation based on angle and radius
        0.7 + turbulent(vec3(uv * 4.0, iTime)) * 0.3, // Saturation with turbulence
        0.5 + sin(r * 5.0 + iTime) * 0.5 // Brightness variation
    ));

    // Add radial color variation
    color *= vec3(1.0 - r * 0.5);

    return vec4(color, turbulent(vec2(uv * 2.0)) * 0.5 + 0.5);
}
