vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to range -1.0 to 1.0
    vec2 st = uv * 2.0 - 1.0;

    // Create a center point (mouse position would go here)
    vec2 center = vec2(0.0);

    // Calculate distance from center
    float dist = length(st);

    // Add circular motion using time and noise
    float angle = atan2(st.y, st.x) + iTime * 0.5;
    float radius = 0.3 + turbulent(vec2(angle, iTime)) * 0.2;

    // Create particle effect
    float d = length(st - vec2(cos(angle), sin(angle)) * radius);
    float glow = step(0.1, radius - d) * (radius - d) / 0.1;

    // Add color variation using HSL
    vec3 color = hsl2rgb(vec3(
        mod(angle + iTime, PI) / PI,
        0.7 + turbulent(vec2(st.x * 2.0, st.y * 2.0)) * 0.3,
        0.5 + glow * 0.5
    ));

    // Add glow effect
    color = mix(color, vec3(1.0), glow);

    return vec4(color, 1.0);
}
