vec4 Effect2(vec2 uv) {
    vec3 color = vec3(0.0);

    // Create multiple rotating squares
    for(float i = 0.0; i < 6.0; i++) {
        for(float j = 0.0; j < 6.0; j++) {
            // Square position and rotation
            vec2 pos = vec2(i, j) * 0.18 - 0.5;
            float time = iTime * (i + j + 1.0) * 0.5;
            float angle = sin(time) * 0.5 + i * 0.2;

            // Rotate coordinates
            vec2 rotatedUv = uv - pos;
            rotatedUv = vec2(
                rotatedUv.x * cos(angle) - rotatedUv.y * sin(angle),
                rotatedUv.x * sin(angle) + rotatedUv.y * cos(angle)
            );

            // Square check
            float d = max(abs(rotatedUv.x), abs(rotatedUv.y));

            // Color cycling
            vec3 squareColor = hsl2rgb(vec3(
                (time + i + j) * 0.1,
                0.7,
                0.5 + turbulent(vec3(uv, time)) * 0.5
            ));

            // Draw squares with smooth edges
            if(d < 0.5) {
                float edge = smoothstep(0.48, 0.5, d);
                color += squareColor * edge;
            }
        }
    }

    return vec4(color, 1.0);
}
