vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar form
    vec2 st = uv * 2.0 - 1.0;
    float r = length(st);
    float theta = atan2(st.y, st.x);
    // Add time-based pulsation
    r += sin(iTime * 0.5 + theta) * 0.3;
    // Create a flowing color pattern using HSL
    vec3 color = hsl(
        (theta / PI + 1.0) * 0.5,   // Hue based on angle
        0.7 + turbulent(vec2(r * 4.0, iTime * 0.2)) * 0.3, // Saturation variation
        0.5 + sin(iTime * 0.2 + r * 10.0) * 0.5          // Brightness variation
    );
    // Add noise texture for detail
    vec2 noiseScale = vec2(4.0, 4.0);
    float n1 = noise(st * noiseScale + iTime);
    float n2 = turbulent(st * noiseScale * 2.0 + iTime);
    // Combine noise and color with light falloff
    float intensity = (1.0 - exp(-r)) * (n1 * 0.5 + 0.5) * (n2 * 0.5 + 0.5);

    return vec4(hsl2rgb(color) * intensity, 1.0);
}
