vec4 Effect2(vec2 uv) {
    // Scale and animate UV coordinates
    vec2 st = uv * 10.0 + vec2(sin(iTime), cos(iTime)) * 5.0;

    // Create rotating color pattern using noise
    float n = noise(st + vec2(iTime));
    vec3 color = hsl(n * 0.6 + 0.3, 0.8, 0.7);

    // Add turbulence for dynamic effect
    float t = turbulent(st + vec2(iTime * 0.5));
    color = mix(color, hsl(0.5, 1.0, 0.5), abs(t * 0.5));

    // Create psychedelic swirl effect
    float angle = atan2(uv.y - 0.5, uv.x - 0.5);
    float dist = length(uv - 0.5);
    color += hsv2rgb(vec3(angle * 0.1 + iTime * 0.1, dist * 0.6 + 0.4, 0.8));

    // Add noise variation
    vec2 noiseSt = st * 0.2;
    float n2 = noise(noiseSt);
    color += hsv2rgb(vec3(n2 * 0.5 + iTime * 0.1, 0.7, 0.6));

    return vec4(color, 1.0);
}
