vec4 Effect2(vec2 uv) {
    // Scale and animate UV coordinates
    vec2 st = uv * 10.0 + vec2(iTime, iTime*0.5);

    // Create sine wave patterns with different frequencies
    float a = sin(st.x * 2.0 + iTime) * cos(st.y + iTime*3.0);
    float b = sin(st.y * 2.0 - iTime*2.0) * cos(st.x + iTime);
    float c = sin(st.x + st.y + iTime*4.0) * turbulent(vec2(uv)*10.0);

    // Combine patterns for RGB channels with color shifting
    vec3 color = vec3(
        sin(a * 0.5 + iTime) * 0.5 + 0.5,
        sin(b * 0.7 - iTime*0.5) * 0.5 + 0.5,
        cos(c * 0.6 + iTime*2.0) * 0.5 + 0.5
    );

    // Add some turbulence for extra movement
    color += vec3(turbulent(vec2(uv)*15.0 + iTime)) * 0.3;

    return vec4(clamp(color, 0.0, 1.0), 1.0);
}
