vec4 Effect2(vec2 uv) {
    // Create a base noise pattern with time offset
    float noise1 = turbulent(uv * 5.0 + iTime);

    // Create shifting grid lines using sine waves
    vec2 st = uv;
    float line1 = sin(st.x * 10.0 + iTime) * 0.5 + 0.5;
    float line2 = cos(st.y * 10.0 + iTime * 0.5) * 0.5 + 0.5;

    // Combine lines with noise for organic movement
    float pattern = (line1 + line2) * 0.5 + noise1 * 0.3;

    // Create color transitions using HSL
    vec3 hslColor = vec3(
        mod(iTime * 0.1 + pattern, 1.0),  // Hue
        0.7 + noise(uv * 2.0) * 0.3,       // Saturation
        0.5 + sin(iTime) * 0.3           // Lightness
    );

    // Convert HSL to RGB and output
    vec3 rgbColor = hsl2rgb(hslColor);

    return vec4(rgbColor, 1.0);
}
