vec4 Effect2(vec2 uv) {
    // Create a grid pattern using sine functions for smooth transitions
    vec2 st = uv * 10.0;
    vec2 ipos = floor(st);
    vec2 fpos = fract(st);

    // Calculate distance from center of each cell
    vec2 center = vec2(0.5, 0.5);
    float dist = distance(center, fpos);

    // Create pulsating effect using sine wave
    float pulse = sin(dist * 10.0 + iTime) * 0.5 + 0.5;

    // Generate color based on position and time
    vec3 hslColor = vec3(
        fract(iTime * 0.2 + ipos.x * 0.1 + ipos.y * 0.2),
        0.7,
        pulse * 0.8 + 0.2
    );

    // Convert to RGB and add noise for texture
    vec3 rgbColor = hsl2rgb(hslColor);
    rgbColor *= sin(iTime) * 0.5 + 0.5;
    rgbColor += noise(vec3(uv * 10.0, iTime)) * 0.2;

    return vec4(rgbColor, 1.0);
}
