vec4 Effect2(vec2 uv) {
    // Create rotating polar coordinates
    vec2 st = uv * 2.0 - 1.0;
    float angle = atan(st.y, st.x) + iTime * 0.5;
    float radius = length(st);

    // Add time-based animation
    vec3 noisePos = vec3(radius * 4.0, angle * 4.0, iTime * 2.0);
    float turb = turbulent(noisePos);

    // Create color variations using HSL
    vec3 hslColor = vec3(
        fract(angle * 0.1 + iTime * 0.1),
        0.7 + turb * 0.3,
        0.5 + sin(iTime) * 0.2
    );

    // Convert to RGB and add noise texture
    vec3 color = hsl2rgb(hslColor);
    color *= 1.0 + turbulent(vec2(uv * 4.0)) * 0.5;

    return vec4(color, 1.0);
}
