vec4 Effect2(vec2 uv) {
    vec3 color = vec3(0.0);
    vec2 st = uv;

    // Create grid pattern
    vec2 grid = floor(st * 10.0);
    float rotation = mod(grid.x + grid.y, 4.0); // Rotate squares in 4 different directions

    // Calculate rotation angle based on time and position
    float angle = (rotation * pi/2.0) + iTime*0.5 + sin(grid.x)*0.3;

    // Apply rotation to UV coordinates
    vec2 rotatedUV = vec2(
        cos(angle)*st.x - sin(angle)*st.y,
        sin(angle)*st.x + cos(angle)*st.y
    );

    // Add noise to create dynamic color shifts
    float noiseValue = noise(vec3(st*10.0, iTime));
    vec3 hslColor = vec3(
        mod(grid.x * 0.2 + grid.y * 0.3 + iTime*0.1, 1.0),
        0.7 + noiseValue*0.3,
        0.8 + sin(iTime)*0.2
    );

    // Convert HSL to RGB and mix with turbulence
    color = hsl(hslColor.x, hslColor.y, hslColor.z);
    color *= 1.0 + turbulent(rotatedUV*5.0 + iTime) * 0.3;

    return vec4(color, 1.0);
}
