vec4 Effect2(vec2 uv) {
    // Create base wave patterns with different frequencies
    float wave1 = sin(uv.x * 4.0 + iTime) + cos(uv.y * 4.0 - iTime);
    float wave2 = sin((uv.x - 0.5) * 6.0 + iTime * 0.5) * cos((uv.y - 0.5) * 6.0 - iTime * 0.5);

    // Add noise and turbulence
    vec3 noisePoint1 = vec3(uv.x, uv.y, iTime * 0.2);
    float turb1 = turbulent(noisePoint1);

    vec3 noisePoint2 = vec3(uv.y, uv.x, iTime * 0.3) + vec3(10.0);
    float turb2 = turbulent(noisePoint2);

    // Combine waves and turbulence
    float combined = (wave1 * 0.5 + wave2 * 0.5) * (turb1 * 0.5 + turb2 * 0.5);

    // Create color variations using HSL
    vec3 hslColor;
    hslColor.x = fract((combined * 0.5 + iTime * 0.1)) * 360.0; // Hue variation over time
    hslColor.y = abs(sin(iTime * 0.2) * 0.7 + 0.3); // Saturation modulation
    hslColor.z = abs(combined * 0.5 + 0.5); // Brightness based on wave pattern

    vec3 rgbColor = hsl2rgb(hslColor);

    return vec4(rgbColor, 1.0);
}
