vec4 Effect2(vec2 uv) {
    // Create rotating patterns using sine waves
    vec2 st = uv * 5.0 + vec2(sin(iTime), cos(iTime)) * 3.0;

    // Add time-based rotation
    float angle = iTime * 0.1;
    st = mat2(cos(angle), -sin(angle),
              sin(angle), cos(angle)) * st;

    // Create color variations using noise and turbulence
    float noiseVal = turbulent(st) + turbulent(vec3(st, iTime*0.5));

    // Generate rotating colors using HSL
    vec3 hslColor = vec3(
        mod(noiseVal + iTime, 1.0),   // Hue variation over time
        0.7 + noiseVal * 0.3,         // Saturation
        0.5 + sin(iTime) * 0.2       // Lightness variation
    );

    vec3 rgbColor = hsl2rgb(hslColor);

    return vec4(rgbColor, 1.0);
}
