vec4 Effect2(vec2 uv) {
    // Create flowing noise patterns with time
    float n = noise(uv * 10.0 + vec2(iTime, iTime));

    // Create positional sine waves with time offset
    float waveX = sin(uv.x * 5.0 + iTime * 2.0);
    float waveY = sin(uv.y * 5.0 + iTime * 3.0);

    // Combine noise and waves for color variation
    vec3 color = hsl(
        mod(iTime * 0.1 + n * 0.5, 1.0),   // Hue variation over time
        0.7 + n * 0.3,                     // Saturation
        0.5 + (waveX + waveY) * 0.2         // Brightness modulation
    );

    // Add turbulence for more complex patterns
    vec2 st = uv * 10.0;
    float turb = turbulent(st + vec2(iTime, iTime));

    // Mix in some RGB components for depth
    color += vec3(
        sin(uv.x * 8.0 + iTime) * 0.5,
        sin(uv.y * 8.0 + iTime * 1.2) * 0.5,
        turb * 0.5
    );

    return vec4(color, 1.0);
}
