vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar form
    vec2 pos = uv - 0.5;
    float dist = length(pos);
    float angle = atan2(pos.y, pos.x) + iTime * 0.1;

    // Add circular motion with noise modulation
    vec2 motion = vec2(sin(angle), cos(angle)) * (0.3 + 0.2 * noise(vec3(uv * 4.0, iTime)));
    vec2 finalPos = uv + motion * dist;

    // Calculate color based on position and time
    float hue = atan2(finalPos.y, finalPos.x) / (2.0 * PI) + 0.5 + iTime * 0.1;
    vec3 color = hsl2rgb(vec3(hue, 1.0, 0.5));

    // Add glow effect
    float glow = max(0.0, 1.0 - length(finalPos - 0.5) * 4.0);
    glow = smoothstep(0.0, 1.0, glow);

    return vec4(color, glow);
}
