vec4 Effect2(vec2 uv) {
    // Create smooth color transitions using sine functions
    float time = iTime * 0.5;

    // Generate base color with time-based hue shift
    vec3 color1 = hsl2rgb(vec3(
        sin(time + uv.x * 2.0) * 0.5 + 0.5,
        0.7,
        0.6
    ));

    // Add turbulence to create dynamic movement
    float turb = turbulent(uv * 4.0 + vec2(sin(time), cos(time)));

    // Create secondary color with offset hue for transition effect
    vec3 color2 = hsl2rgb(vec3(
        sin(time + uv.y * 2.0) * 0.5 + 0.5,
        0.7,
        0.6
    ));

    // Mix colors based on turbulence and position
    vec3 finalColor = mix(color1, color2, abs(turb * 0.5 + 0.5));

    return vec4(finalColor, 1.0);
}
