vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar form for rotation
    float r = length(uv);
    float theta = atan2(uv.y, uv.x) + iTime;  // Add time for rotation

    // Create a checkerboard pattern using polar coordinates
    vec2 st = vec2(r * cos(theta), r * sin(theta));
    float frequency = 10.0;
    float cell = floor(st.x * frequency);

    // Alternate colors based on cell position and time
    vec3 colorA = hsl2rgb(vec3(
        mod(cell + iTime, 1.0) * 360.0,
        0.5,
        0.5
    ));

    vec3 colorB = hsl2rgb(vec3(
        mod(cell + iTime + 0.5, 1.0) * 360.0,
        0.5,
        0.5
    ));

    // Use turbulence to create dynamic color transitions
    float noiseA = turbulent(vec3(st.x, st.y, iTime));
    float noiseB = turbulent(vec3(st.x + 1.0, st.y + 1.0, iTime));

    vec3 finalColor = mix(colorA, colorB, smoothstep(0.0, 1.0, noiseA * 0.5 + 0.5));

    return vec4(finalColor, 1.0);
}
