vec4 Effect2(vec2 uv) {
    // Create time-based animation
    float time = iTime * 0.5;

    // Remap UV coordinates to (-1,-1) to (1,1)
    vec2 st = uv * 2.0 - 1.0;

    // Add some movement using sine wave
    vec2 movement = vec2(sin(time * 0.5), cos(time));
    st += movement * 0.3;

    // Create a base color using HSL
    vec3 baseColor = hsl(0.5, 1.0, 0.5);

    // Add noise and turbulence for dynamic patterns
    float noiseValue = noise(vec2(st.x + time, st.y));
    float turbulentValue = turbulent(vec3(st.x + time, st.y, time * 0.1));

    // Combine colors using HSL to RGB conversion
    vec3 color = hsl2rgb(vec3(
        0.5 + noiseValue * 0.4,
        1.0 - abs(turbulentValue) * 0.6,
        0.5 + turbulentValue * 0.3
    ));

    // Mix base color with dynamic color
    color = mix(baseColor, color, 0.7);

    return vec4(color, 1.0);
}
